def pick_name(random):
  consonnants = "b,c,d,f,g,j,k,l,m,n,p,r,s,t,v,w,y".split(",")
  # ,br,bl,ch,cl,ck,cr,dr,dy,fr,gr,ly,mn,ny,pl,pr,rh,sh,tr,vr
  vowels = "a,e,i,o,u".split(",")

  word = ''
  for i in range(random.randint(2,4)):
      word += random.choice(consonnants)
      word += random.choice(vowels)

  return word.capitalize()

def pick_emoji_name_set_seed(random, seed):
  random.seed(seed)
  emoji = pick_emoji(random)
  name = pick_name(random)
  random.seed()
  return (emoji, name)

